(function(t){
    t.fn.btnLoadmore=function(e){
        var n={
            showItem:3,
            whenClickBtn:3,
            textBtn:"Load More",
            classBtn:"theme-btn btn-style-one",
            setCookies:!1
        },
            e=t.extend(n,e);
            this.each(function(){
                var e=t(this),
                o=t(e.children());
                
                o.hide(),
                o.slice(0,n.showItem).show(),
                // o.filter(":hidden")> 0 &&
                e.after('<div class="text-center"><button type="button" class="btn-loadmore '+n.classBtn+'"><span>'+n.textBtn+"</span></button></div>"),
                t(document).on("click",".btn-loadmore",function(e){
                    e.preventDefault(),
                    o.filter(":hidden").slice(0,n.whenClickBtn).slideDown(),
                    0==o.filter(":hidden").length&&t(".btn-loadmore").fadeOut("slow")
                })
            })
        }
    })(jQuery);